import { v4 as uuidv4 } from 'uuid';
import AWS from "aws-sdk";

const dynamoDb = new AWS.DynamoDB.DocumentClient();

export const main = async (event) => {
  const {
    name,
    price,
    description,
    s3ImageUrl,
  } = typeof event.body === 'string' ? JSON.parse(event.body) : event.body;

  const params = {
    id: uuidv4(),
    name,
    price,
    description,
    s3ImageUrl,
  };

  try {
    const product = await dynamoDb.put(params).promise();

    return {
      statusCode: 200,
      body: {
        message: 'Success in crate new product',
        name: product.name,
      },
    };
  } catch (error) {
    console.log(error);
    return {
      statusCode: 500,
      body: {
        error: error.messag,
      },
    };
  }
};
