import * as uuid from "uuid";
import AWS from "aws-sdk";

const dynamoDb = new AWS.DynamoDB.DocumentClient();

export const main = async (event) => {
  const {
    name,
    price,
    description,
    s3ImageUrl,
  } = typeOf(event.body) === 'String' ? event.body : JSON.parse(event.body);

  const params = {
    id: uuid(),
    name,
    price,
    description,
    s3ImageUrl,
  };

  try {
    const product = await dynamoDb.put(params).promise();

    return {
      statusCode: 200,
      body: {
        message: 'Success in crate new product',
        name: product.name,
      },
    };
  } catch (error) {
    return {
      statusCode: 500,
      body: {
        error: error.messag,
      },
    };
  }
};
